krpano.action:
if(!hotspot[%1], exit);

calc(fy, hotspot[%1].frame * 64);
set(hotspot[%1].crop, calc:'0|' + fy + '|56|64');

inc(hotspot[%1].frame);
if(hotspot[%1].frame GE 32, set(hotspot[%1].frame, 0));

delayedcall(0.06, hs_play_loop(%1));

set(layer[info_panel].visible, true);
set(layer[info_panel].enabled, true);
set(layer[info_panel].alpha, 1.0);

set(layer[info_panel_title].html, get:data[%1].content);
set(layer[info_panel_text].html,  get:data[%2].content);

set(layer[info_panel_scroll].scrollposy, 0);
layer[info_panel_scroll].update();


set(gallery.count,%1);

if(%1 GE 1, set(gallery.img1,%2); );
if(%1 GE 2, set(gallery.img2,%3); );
if(%1 GE 3, set(gallery.img3,%4); );
if(%1 GE 4, set(gallery.img4,%5); );
if(%1 GE 5, set(gallery.img5,%6); );
if(%1 GE 6, set(gallery.img6,%7); );
if(%1 GE 7, set(gallery.img7,%8); );

set(gallery.index,1);

set(layer[gallery_bg].visible,true);
set(layer[gallery_bg].enabled,true);

set(layer[gallery_img].onloaded, null);
set(layer[gallery_img].alpha, 0.0);
set(layer[gallery_img].url, null);


if(gallery.count LT 2,
set(layer[gallery_prev_btn].visible,false);
set(layer[gallery_next_btn].visible,false);
set(layer[gallery_prev_hit].visible,false);
set(layer[gallery_next_hit].visible,false);
,
set(layer[gallery_prev_btn].visible,true);
set(layer[gallery_next_btn].visible,true);
set(layer[gallery_prev_hit].visible,true);
set(layer[gallery_next_hit].visible,true);
);

delayedcall(0.01, gallery_show(); );

if(gallery.index == 1, set(layer[gallery_img].url,get(gallery.img1)); );
if(gallery.index == 2, set(layer[gallery_img].url,get(gallery.img2)); );
if(gallery.index == 3, set(layer[gallery_img].url,get(gallery.img3)); );
if(gallery.index == 4, set(layer[gallery_img].url,get(gallery.img4)); );
if(gallery.index == 5, set(layer[gallery_img].url,get(gallery.img5)); );
if(gallery.index == 6, set(layer[gallery_img].url,get(gallery.img6)); );
if(gallery.index == 7, set(layer[gallery_img].url,get(gallery.img7)); );

set(layer[gallery_img].alpha, 0.0);
set(layer[gallery_img].visible, true);
set(layer[gallery_img].onloaded, gallery_fit_and_fade(); );

gallery_caption_show();

calc(gallery.maxw, stagewidth  * 0.7);
calc(gallery.maxh, stageheight * 0.7);

calc(gallery.sw, gallery.maxw / layer[gallery_img].imagewidth);
calc(gallery.sh, gallery.maxh / layer[gallery_img].imageheight);

calc(gallery.s, min(gallery.sw, gallery.sh));
calc(gallery.s, min(gallery.s, 1.0));  

set(layer[gallery_img].scale, get(gallery.s));

if(gallery.count LT 2, exit);
inc(gallery.index);
if(gallery.index GT gallery.count, set(gallery.index,1));
gallery_show();

if(gallery.count LT 2, exit);
dec(gallery.index);
if(gallery.index LT 1, set(gallery.index,get(gallery.count)));
gallery_show();

set(gallery.cap1, %1);
set(gallery.cap2, %2);
set(gallery.cap3, %3);
set(gallery.cap4, %4);
set(gallery.cap5, %5);
set(gallery.cap6, %6);
set(gallery.cap7, %7);

if(gallery.index == 1,
if(gallery.cap1, set(layer[gallery_caption].html, get(data[get(gallery.cap1)].content)); , set(layer[gallery_caption].html,''); );
);
if(gallery.index == 2,
if(gallery.cap2, set(layer[gallery_caption].html, get(data[get(gallery.cap2)].content)); , set(layer[gallery_caption].html,''); );
);
if(gallery.index == 3,
if(gallery.cap3, set(layer[gallery_caption].html, get(data[get(gallery.cap3)].content)); , set(layer[gallery_caption].html,''); );
);
if(gallery.index == 4,
if(gallery.cap4, set(layer[gallery_caption].html, get(data[get(gallery.cap4)].content)); , set(layer[gallery_caption].html,''); );
);
if(gallery.index == 5,
if(gallery.cap5, set(layer[gallery_caption].html, get(data[get(gallery.cap5)].content)); , set(layer[gallery_caption].html,''); );
);
if(gallery.index == 6,
if(gallery.cap6, set(layer[gallery_caption].html, get(data[get(gallery.cap6)].content)); , set(layer[gallery_caption].html,''); );
);
if(gallery.index == 7,
if(gallery.cap7, set(layer[gallery_caption].html, get(data[get(gallery.cap7)].content)); , set(layer[gallery_caption].html,''); );
);

set(layer[gallery_bg].visible,false);
set(layer[gallery_bg].enabled,false);


set(layer[gallery_img].onloaded, null);
set(layer[gallery_img].url, null);
set(layer[gallery_img].alpha, 1.0);
set(layer[gallery_img].scale, 1.0);

set(layer[gallery_caption].html, '');

events.onremovescene:
			  close_all_articles();
  close_gallery();

  if(hotspot[hsp_2_video], hotspot[hsp_2_video].stop(); );
  if(hotspot[hsp_10_2_video], hotspot[hsp_10_2_video].stop(); );

hotspot.onclick:
set_gallery_caps_from_data(cap_g7_3, cap_g7_3, cap_g7_3, cap_g7_3, cap_g7_3);
open_gallery(5,
  'images/gallery/7_3_1.jpg',
  'images/gallery/7_3_2.jpg',
  'images/gallery/7_3_3.jpg',
  'images/gallery/7_3_4.jpg',
  'images/gallery/7_3_5.jpg'
);
