krpano.action:
if(!hotspot[%1], exit);

calc(fy, hotspot[%1].frame * 64);
set(hotspot[%1].crop, calc:'0|' + fy + '|56|64');

inc(hotspot[%1].frame);
if(hotspot[%1].frame GE 32, set(hotspot[%1].frame, 0));

delayedcall(0.06, hs_play_loop(%1));

copy(drag_currenty, y);
copy(drag_stagey, mouse.stagey);
calc(y_min, layer[get(parent)].pixelheight - pixelheight);

if(y_min LT 0,
	asyncloop(pressed,
		calc(y, drag_currenty + (mouse.stagey - drag_stagey));
		clamp(y, get(y_min), 0);
	);
);

set(layer[info_panel].visible, true);
set(layer[info_panel].enabled, true); set(layer[info_panel].alpha, 1.0);

set(layer[info_panel_title].html, get:data[%1].content);
