set(layer[info_panel_text].y, 0);

delayedcall(0.2, 
    layer[info_panel_title].update();
    layer[info_panel_text].update();
    calc(layer[info_panel_mask].y, layer[info_panel_title].y + layer[info_panel_title].pixelheight + 30);
    calc(layer[info_panel_mask].height, -30 - get(layer[info_panel_mask].y));
);

set(layer[info_panel].alpha, 0.0);
set(layer[info_panel].visible, false);
set(layer[info_panel].enabled, false);
stoptween(layer[info_panel].alpha);


