copy(drag_currenty, y);
copy(drag_stagey, mouse.stagey);
calc(y_min, layer[get(parent)].pixelheight - pixelheight);

if(y_min LT 0,
    asyncloop(pressed,
        calc(y, drag_currenty + (mouse.stagey - drag_stagey));
        clamp(y, get(y_min), 0);
    );
);

if(gallery.index == 1, set(layer[gallery_img].url, get(gallery.img1)); );
if(gallery.index == 2, set(layer[gallery_img].url, get(gallery.img2)); );
if(gallery.index == 3, set(layer[gallery_img].url, get(gallery.img3)); );
